import React, {Component} from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import { robots } from './robots';
import './App.css'


class App extends Component {
	constructor() {
		super()
		this.state = {
			robots: [],
			searchField:''
		}
	}

	componentDidMount (){
		fetch('https://jsonplaceholder.typicode.com/users')
		.then(response =>{
			response.json();
		})
